#!/bin/bash

git clone https://github.com/My-TRAC/data-model.git
cd data-model/Resources/MYSQLInitDataModel/app
mvn assembly::assembly
cp target/MYSQLInitDataModel-1.0-SNAPSHOT-jar-with-dependencies.jar /root/MYSQLInitDataModel-1.0-SNAPSHOT-jar-with-dependencies.jar
cd /root


git clone https://github.com/My-TRAC/ConfigurationScripts.git
chmod +x ./ConfigurationScripts/*.sh

/root/ConfigurationScripts/waitForSchemaRegistry.sh
/root/ConfigurationScripts/waitForKafkaConenct.sh
/root/ConfigurationScripts/waitForMySQL.sh

sleep 60

java -cp /root/MYSQLInitDataModel-1.0-SNAPSHOT-jar-with-dependencies.jar MYSQLInitDataModel --username $MYSQL_USER --password $MYSQL_PASSWORD --database $MYSQL_DATABASE --topic-names activity,mobility_trace,facility,poi,user,user_chooses_route,user_evaluates_activity,user_joins_group,user_uses_app,user_views_activity,user_reward,route_choice_model_output,route_departure_model_output,poll_questionary,poll_response,user_email,user_twitter_account,otp_reply --schema-registry http://$SCHEMA_REGISTRY_HOST_NAME:8081 --mysql $MYSQL_HOST':'$MYSQL_PORT


sleep 20

